                            // Renommer le fichier pour supprimer les caractères spéciaux
                            var newFileName = decodeURI(file.name).replace(/[^\w.\s\d\-_]/gi, '').replace(/\s+/g, '_'); // \s pour espace
                            
                            // Vérifier si le nom du fichier a été modifié
